package 확인문제;

public class 문제풀이2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 30;
		int num2 = 40;
		
		System.out.println("두 수를 더한 값은 얼마? => " + (num1 + num2));
		System.out.println("num1을 num2로 나눈 값은 얼마? => " + (num1 / num2));
		System.out.println("num1을 num2로 나눈 나머지 값은 얼마? => " + (num1 % num2));
	}

}
