package 조건문;

public class IF문 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 0;
		
		if (x == 0) {
			System.out.println("x 는 0 과 같다.");
		} else {
			System.out.println("x 는 0과 같지 않다.");
		}
		if (x == 1) {
			System.out.println("x 는 1과 같다.");
		}
	}

}
